                  <div class="highlight"><pre><div class='line' id='LC1'><span class="o">&lt;?</span><span class="nx">php</span> <span class="nb">defined</span><span class="p">(</span><span class="s1">&#39;SYSPATH&#39;</span><span class="p">)</span> <span class="k">or</span> <span class="k">die</span><span class="p">(</span><span class="s1">&#39;No direct script access.&#39;</span><span class="p">);</span></div><div class='line' id='LC2'><br/></div><div class='line' id='LC3'><span class="c1">// -- Environment setup --------------------------------------------------------</span></div><div class='line' id='LC4'><br/></div><div class='line' id='LC5'><span class="c1">// Load the core Kohana class</span></div><div class='line' id='LC6'><span class="k">require</span> <span class="nx">SYSPATH</span><span class="o">.</span><span class="s1">&#39;classes/Kohana/Core&#39;</span><span class="o">.</span><span class="nx">EXT</span><span class="p">;</span></div><div class='line' id='LC7'><br/></div><div class='line' id='LC8'><span class="k">if</span> <span class="p">(</span><span class="nb">is_file</span><span class="p">(</span><span class="nx">APPPATH</span><span class="o">.</span><span class="s1">&#39;classes/Kohana&#39;</span><span class="o">.</span><span class="nx">EXT</span><span class="p">))</span></div><div class='line' id='LC9'><span class="p">{</span></div><div class='line' id='LC10'>	<span class="c1">// Application extends the core</span></div><div class='line' id='LC11'>	<span class="k">require</span> <span class="nx">APPPATH</span><span class="o">.</span><span class="s1">&#39;classes/Kohana&#39;</span><span class="o">.</span><span class="nx">EXT</span><span class="p">;</span></div><div class='line' id='LC12'><span class="p">}</span></div><div class='line' id='LC13'><span class="k">else</span></div><div class='line' id='LC14'><span class="p">{</span></div><div class='line' id='LC15'>	<span class="c1">// Load empty core extension</span></div><div class='line' id='LC16'>	<span class="k">require</span> <span class="nx">SYSPATH</span><span class="o">.</span><span class="s1">&#39;classes/Kohana&#39;</span><span class="o">.</span><span class="nx">EXT</span><span class="p">;</span></div><div class='line' id='LC17'><span class="p">}</span></div><div class='line' id='LC18'><br/></div><div class='line' id='LC19'><span class="sd">/**</span></div><div class='line' id='LC20'><span class="sd"> * Set the default time zone.</span></div><div class='line' id='LC21'><span class="sd"> *</span></div><div class='line' id='LC22'><span class="sd"> * @link http://kohanaframework.org/guide/using.configuration</span></div><div class='line' id='LC23'><span class="sd"> * @link http://www.php.net/manual/timezones</span></div><div class='line' id='LC24'><span class="sd"> */</span></div><div class='line' id='LC25'><span class="nb">date_default_timezone_set</span><span class="p">(</span><span class="s1">&#39;America/Chicago&#39;</span><span class="p">);</span></div><div class='line' id='LC26'><br/></div><div class='line' id='LC27'><span class="sd">/**</span></div><div class='line' id='LC28'><span class="sd"> * Set the default locale.</span></div><div class='line' id='LC29'><span class="sd"> *</span></div><div class='line' id='LC30'><span class="sd"> * @link http://kohanaframework.org/guide/using.configuration</span></div><div class='line' id='LC31'><span class="sd"> * @link http://www.php.net/manual/function.setlocale</span></div><div class='line' id='LC32'><span class="sd"> */</span></div><div class='line' id='LC33'><span class="nb">setlocale</span><span class="p">(</span><span class="nx">LC_ALL</span><span class="p">,</span> <span class="s1">&#39;en_US.utf-8&#39;</span><span class="p">);</span></div><div class='line' id='LC34'><br/></div><div class='line' id='LC35'><span class="sd">/**</span></div><div class='line' id='LC36'><span class="sd"> * Enable the Kohana auto-loader.</span></div><div class='line' id='LC37'><span class="sd"> *</span></div><div class='line' id='LC38'><span class="sd"> * @link http://kohanaframework.org/guide/using.autoloading</span></div><div class='line' id='LC39'><span class="sd"> * @link http://www.php.net/manual/function.spl-autoload-register</span></div><div class='line' id='LC40'><span class="sd"> */</span></div><div class='line' id='LC41'><span class="nb">spl_autoload_register</span><span class="p">(</span><span class="k">array</span><span class="p">(</span><span class="s1">&#39;Kohana&#39;</span><span class="p">,</span> <span class="s1">&#39;auto_load&#39;</span><span class="p">));</span></div><div class='line' id='LC42'><br/></div><div class='line' id='LC43'><span class="sd">/**</span></div><div class='line' id='LC44'><span class="sd"> * Optionally, you can enable a compatibility auto-loader for use with</span></div><div class='line' id='LC45'><span class="sd"> * older modules that have not been updated for PSR-0.</span></div><div class='line' id='LC46'><span class="sd"> *</span></div><div class='line' id='LC47'><span class="sd"> * It is recommended to not enable this unless absolutely necessary.</span></div><div class='line' id='LC48'><span class="sd"> */</span></div><div class='line' id='LC49'><span class="c1">//spl_autoload_register(array(&#39;Kohana&#39;, &#39;auto_load_lowercase&#39;));</span></div><div class='line' id='LC50'><br/></div><div class='line' id='LC51'><span class="sd">/**</span></div><div class='line' id='LC52'><span class="sd"> * Enable the Kohana auto-loader for unserialization.</span></div><div class='line' id='LC53'><span class="sd"> *</span></div><div class='line' id='LC54'><span class="sd"> * @link http://www.php.net/manual/function.spl-autoload-call</span></div><div class='line' id='LC55'><span class="sd"> * @link http://www.php.net/manual/var.configuration#unserialize-callback-func</span></div><div class='line' id='LC56'><span class="sd"> */</span></div><div class='line' id='LC57'><span class="nb">ini_set</span><span class="p">(</span><span class="s1">&#39;unserialize_callback_func&#39;</span><span class="p">,</span> <span class="s1">&#39;spl_autoload_call&#39;</span><span class="p">);</span></div><div class='line' id='LC58'><br/></div><div class='line' id='LC59'><span class="c1">// -- Configuration and initialization -----------------------------------------</span></div><div class='line' id='LC60'><br/></div><div class='line' id='LC61'><span class="sd">/**</span></div><div class='line' id='LC62'><span class="sd"> * Set the default language</span></div><div class='line' id='LC63'><span class="sd"> */</span></div><div class='line' id='LC64'><span class="nx">I18n</span><span class="o">::</span><span class="na">lang</span><span class="p">(</span><span class="s1">&#39;en-us&#39;</span><span class="p">);</span></div><div class='line' id='LC65'><br/></div><div class='line' id='LC66'><span class="sd">/**</span></div><div class='line' id='LC67'><span class="sd"> * Set Kohana::$environment if a &#39;KOHANA_ENV&#39; environment variable has been supplied.</span></div><div class='line' id='LC68'><span class="sd"> *</span></div><div class='line' id='LC69'><span class="sd"> * Note: If you supply an invalid environment name, a PHP warning will be thrown</span></div><div class='line' id='LC70'><span class="sd"> * saying &quot;Couldn&#39;t find constant Kohana::&lt;INVALID_ENV_NAME&gt;&quot;</span></div><div class='line' id='LC71'><span class="sd"> */</span></div><div class='line' id='LC72'><span class="k">if</span> <span class="p">(</span><span class="nb">isset</span><span class="p">(</span><span class="nv">$_SERVER</span><span class="p">[</span><span class="s1">&#39;KOHANA_ENV&#39;</span><span class="p">]))</span></div><div class='line' id='LC73'><span class="p">{</span></div><div class='line' id='LC74'>	<span class="nx">Kohana</span><span class="o">::</span><span class="nv">$environment</span> <span class="o">=</span> <span class="nb">constant</span><span class="p">(</span><span class="s1">&#39;Kohana::&#39;</span><span class="o">.</span><span class="nx">strtoupper</span><span class="p">(</span><span class="nv">$_SERVER</span><span class="p">[</span><span class="s1">&#39;KOHANA_ENV&#39;</span><span class="p">]));</span></div><div class='line' id='LC75'><span class="p">}</span></div><div class='line' id='LC76'><br/></div><div class='line' id='LC77'><span class="sd">/**</span></div><div class='line' id='LC78'><span class="sd"> * Initialize Kohana, setting the default options.</span></div><div class='line' id='LC79'><span class="sd"> *</span></div><div class='line' id='LC80'><span class="sd"> * The following options are available:</span></div><div class='line' id='LC81'><span class="sd"> *</span></div><div class='line' id='LC82'><span class="sd"> * - string   base_url    path, and optionally domain, of your application   NULL</span></div><div class='line' id='LC83'><span class="sd"> * - string   index_file  name of your index file, usually &quot;index.php&quot;       index.php</span></div><div class='line' id='LC84'><span class="sd"> * - string   charset     internal character set used for input and output   utf-8</span></div><div class='line' id='LC85'><span class="sd"> * - string   cache_dir   set the internal cache directory                   APPPATH/cache</span></div><div class='line' id='LC86'><span class="sd"> * - integer  cache_life  lifetime, in seconds, of items cached              60</span></div><div class='line' id='LC87'><span class="sd"> * - boolean  errors      enable or disable error handling                   TRUE</span></div><div class='line' id='LC88'><span class="sd"> * - boolean  profile     enable or disable internal profiling               TRUE</span></div><div class='line' id='LC89'><span class="sd"> * - boolean  caching     enable or disable internal caching                 FALSE</span></div><div class='line' id='LC90'><span class="sd"> * - boolean  expose      set the X-Powered-By header                        FALSE</span></div><div class='line' id='LC91'><span class="sd"> */</span></div><div class='line' id='LC92'><span class="nx">Kohana</span><span class="o">::</span><span class="na">init</span><span class="p">(</span><span class="k">array</span><span class="p">(</span></div><div class='line' id='LC93'>	<span class="s1">&#39;base_url&#39;</span>   <span class="o">=&gt;</span> <span class="s1">&#39;/kohana/&#39;</span><span class="p">,</span></div><div class='line' id='LC94'><span class="p">));</span></div><div class='line' id='LC95'><br/></div><div class='line' id='LC96'><span class="sd">/**</span></div><div class='line' id='LC97'><span class="sd"> * Attach the file write to logging. Multiple writers are supported.</span></div><div class='line' id='LC98'><span class="sd"> */</span></div><div class='line' id='LC99'><span class="nx">Kohana</span><span class="o">::</span><span class="nv">$log</span><span class="o">-&gt;</span><span class="na">attach</span><span class="p">(</span><span class="k">new</span> <span class="nx">Log_File</span><span class="p">(</span><span class="nx">APPPATH</span><span class="o">.</span><span class="s1">&#39;logs&#39;</span><span class="p">));</span></div><div class='line' id='LC100'><br/></div><div class='line' id='LC101'><span class="sd">/**</span></div><div class='line' id='LC102'><span class="sd"> * Attach a file reader to config. Multiple readers are supported.</span></div><div class='line' id='LC103'><span class="sd"> */</span></div><div class='line' id='LC104'><span class="nx">Kohana</span><span class="o">::</span><span class="nv">$config</span><span class="o">-&gt;</span><span class="na">attach</span><span class="p">(</span><span class="k">new</span> <span class="nx">Config_File</span><span class="p">);</span></div><div class='line' id='LC105'><br/></div><div class='line' id='LC106'><span class="sd">/**</span></div><div class='line' id='LC107'><span class="sd"> * Enable modules. Modules are referenced by a relative or absolute path.</span></div><div class='line' id='LC108'><span class="sd"> */</span></div><div class='line' id='LC109'><span class="nx">Kohana</span><span class="o">::</span><span class="na">modules</span><span class="p">(</span><span class="k">array</span><span class="p">(</span></div><div class='line' id='LC110'>	<span class="c1">// &#39;auth&#39;       =&gt; MODPATH.&#39;auth&#39;,       // Basic authentication</span></div><div class='line' id='LC111'>	<span class="c1">// &#39;cache&#39;      =&gt; MODPATH.&#39;cache&#39;,      // Caching with multiple backends</span></div><div class='line' id='LC112'>	<span class="c1">// &#39;codebench&#39;  =&gt; MODPATH.&#39;codebench&#39;,  // Benchmarking tool</span></div><div class='line' id='LC113'>	<span class="c1">// &#39;database&#39;   =&gt; MODPATH.&#39;database&#39;,   // Database access</span></div><div class='line' id='LC114'>	<span class="c1">// &#39;image&#39;      =&gt; MODPATH.&#39;image&#39;,      // Image manipulation</span></div><div class='line' id='LC115'>	<span class="c1">// &#39;minion&#39;     =&gt; MODPATH.&#39;minion&#39;,     // CLI Tasks</span></div><div class='line' id='LC116'>	<span class="c1">// &#39;orm&#39;        =&gt; MODPATH.&#39;orm&#39;,        // Object Relationship Mapping</span></div><div class='line' id='LC117'>	<span class="c1">// &#39;unittest&#39;   =&gt; MODPATH.&#39;unittest&#39;,   // Unit testing</span></div><div class='line' id='LC118'>	<span class="c1">// &#39;userguide&#39;  =&gt; MODPATH.&#39;userguide&#39;,  // User guide and API documentation</span></div><div class='line' id='LC119'>	<span class="p">));</span></div><div class='line' id='LC120'><br/></div><div class='line' id='LC121'><span class="sd">/**</span></div><div class='line' id='LC122'><span class="sd"> * Set the routes. Each route must have a minimum of a name, a URI and a set of</span></div><div class='line' id='LC123'><span class="sd"> * defaults for the URI.</span></div><div class='line' id='LC124'><span class="sd"> */</span></div><div class='line' id='LC125'><span class="nx">Route</span><span class="o">::</span><span class="na">set</span><span class="p">(</span><span class="s1">&#39;default&#39;</span><span class="p">,</span> <span class="s1">&#39;(&lt;controller&gt;(/&lt;action&gt;(/&lt;id&gt;)))&#39;</span><span class="p">)</span></div><div class='line' id='LC126'>	<span class="o">-&gt;</span><span class="na">defaults</span><span class="p">(</span><span class="k">array</span><span class="p">(</span></div><div class='line' id='LC127'>		<span class="s1">&#39;controller&#39;</span> <span class="o">=&gt;</span> <span class="s1">&#39;welcome&#39;</span><span class="p">,</span></div><div class='line' id='LC128'>		<span class="s1">&#39;action&#39;</span>     <span class="o">=&gt;</span> <span class="s1">&#39;index&#39;</span><span class="p">,</span></div><div class='line' id='LC129'>	<span class="p">));</span></div></pre></div>
